import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from 'react-router-dom';
import { Dropdown } from 'react-bootstrap';
import image from './nps.jpeg';
import './Activities.css';

function Activities(props) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  let history = useHistory();

  const ButtonPressed = (props) => {
    history.push({
      pathname: '/parks/' + props,
      state: { props },
    });
  };

  const request =
    'https://developer.nps.gov/api/v1/activities?limit=50&start=0&api_key=pwVAaI7eyJ3qGEpakWoicbKA0kCPdzM6KVyeH2mB';
  useEffect(async () => {
    const response = await fetch(request);
    const info = await response.json();
    setData(info.data);
    setLoading(false);
  }, []);

  return (
    <div>
      <img id="nps" src={image}></img>
      <div>
        <h1 id="title">Activity Menu</h1>
        <p>
          Select from a wide range of activities offered at our national parks
          and locations!s
        </p>
      </div>

      {loading ? (
        <div>..loading</div>
      ) : (
        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            Select Activities
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {data.map((data) => (
              <div>
                <br></br>{' '}
                <Dropdown.Item onClick={() => ButtonPressed(data.name)}>
                  {data.name}
                </Dropdown.Item>
              </div>
            ))}{' '}
          </Dropdown.Menu>
        </Dropdown>
      )}
    </div>
  );
}

export default Activities;
