import React from 'react';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { Button, Dropdown } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import Park from './Park';
import image from './nps.jpeg';
import './Parks.css';

function Parks() {
  let history = useHistory();

  const { activityName } = useParams();
  const request =
    'https://developer.nps.gov/api/v1/activities/parks?q=' +
    activityName +
    '&api_key=pwVAaI7eyJ3qGEpakWoicbKA0kCPdzM6KVyeH2mB';

  const [park, setPark] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(async () => {
    const response = await fetch(request);
    const info = await response.json();
    console.log(info.data[0].parks);
    const item = info.data[0].parks;
    setPark(item);
    setLoading(false);
  }, []);

  const ButtonPressed = (props) => {
    history.push({
      pathname: '/locations/' + props,
      state: { props },
    });
  };

  return (
    <div className="containerL">
      <h1 id="title">
        Showing Results for National Parks that Offer: {activityName}
      </h1>

      {loading ? (
        <div>..loading</div>
      ) : (
        <Dropdown id="drop">
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            Select Parks
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {park.map((park) => (
              <div>
                <br></br>{' '}
                <Button
                  className="parkButton"
                  onClick={() => ButtonPressed(park.parkCode)}
                >
                  {park.fullName}
                </Button>
              </div>
            ))}{' '}
          </Dropdown.Menu>
        </Dropdown>
      )}
    </div>
  );
}

export default Parks;
